import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import API from '../services/api';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      const { data } = await API.get('/employees');
      setEmployees(data);
    };
    fetchEmployees();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      await API.delete(`/employees/${id}`);
      setEmployees(employees.filter((emp) => emp._id !== id));
    }
  };

  return (
    <div className="container mt-5">
      <h2>Employee List</h2>
      <Link to="/employees/add" className="btn btn-primary mb-3">Add Employee</Link>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Name</th>
            <th>Position</th>
            <th>Department</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((emp) => (
            <tr key={emp._id}>
              <td>{emp.firstName} {emp.lastName}</td>
              <td>{emp.position}</td>
              <td>{emp.department}</td>
              <td>{emp.email}</td>
              <td>
                <Link to={`/employees/${emp._id}`} className="btn btn-info mr-2">View</Link>
                <Link to={`/employees/edit/${emp._id}`} className="btn btn-warning mr-2">Edit</Link>
                <button className="btn btn-danger" onClick={() => handleDelete(emp._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeList;
